import re

class AffiliateUrlRewriter:

    def __init__(self):
        self.affiliate_codes = {
            'www.google.com': 'abc123',
            'www.bing.com': 'def456',
            'search.yahoo.com': 'ghi789'
        }

        self.search_engine_pattern = re.compile(r'https?://(www\.google\.com|www\.bing\.com|search\.yahoo\.com)')

    def rewrite(self, url):
        if self.search_engine_pattern.match(url):
            # Get hostname
            hostname = url.split('/')[2]

            # Lookup affiliate code
