import os
from pathlib import Path

def get_all_files(folder):
    files = []
    for path in Path(folder).glob('**/*'):
        if path.is_file():
            files.append(path)
    return files

def generate_report(files):
    report = {}
    for f in files:
        size = os.path.getsize(f)
        report[f] = size
    return report

def save_report(report, fpath):
    os.chmod(fpath, 0o600)
    with open(fpath, 'w') as f:
        f.write('path,bytes\n')
        for path, size in report.items():
            f.write(f'{path},{size}\n')

if __name__ == '__main__':
    folder = '/home/user/data'
    files = get_all_files(folder)
    report = generate_report(files)
    save_report(report, 'report.csv')

