import re

class BusinessDirectory:
    def __init__(self):
        self.businesses = []

    def add(self, name, url, location, hours, services):
        url_regex = re.compile(re.escape(url))

        business = {
            "name": name,
            "url": url,
            "url_regex": url_regex,
            "location": location,
            "hours": hours,
            "services": services
        }
        self.businesses.append(business)

    def search(self, url):
        for business in self.businesses:
            if business["url_regex"].match(url):
                return business

        return None

