import re
import urllib.parse

def extract_urls(text):
    urls = re.findall(r'https?://[^\s]+', text)
    valid_urls = []
    for url in urls:
        parsed = urllib.parse.urlparse(url)
        if parsed.scheme in {"http", "https"} and parsed.netloc:
            valid_urls.append(url)
    return valid_urls

