from urllib.parse import unquote, urlencode

def build_url(base_url, path='', query_params={}):
    url = unquote(base_url) + path

    if query_params:
        query_string = urlencode(query_params)
        url += '?' + query_string

    # Validate URL
    if not url.startswith('http'):
        return None
    elif ' ' in url or '\t' in url or '\n' in url:
        return None
    elif '//' in url[url.index('://')+3:]:
        return None

    return url

