# Search rides.py

import sqlite3
from flask import Flask, request, jsonify

app = Flask(__name__)

@app.route('/search_rides', methods=['POST'])
def search_rides():
    source = request.form['source']
    destination = request.form['destination']
    date = request.form['date']

    with sqlite3.connect('rides.db') as conn:
        cursor = conn.cursor()

        query = "SELECT * FROM rides WHERE source = ? AND destination = ? AND date = ?"
        cursor.execute(query, (source, destination, date))

        rides = cursor.fetchall()

        if rides:
            return jsonify(rides)
        else:
            return jsonify({'message': 'No rides found'})

if __name__ == '__main__':
    app.run()

