# Generate monthly report.py

import sqlite3
from flask import Flask, request, jsonify

app = Flask(__name__)

@app.route('/monthly_report', methods=['POST'])
def monthly_report():
    month = request.form['month']
    year = request.form['year']

    with sqlite3.connect('rides.db') as conn:
        cursor = conn.cursor()

        # Get total earnings for given month/year
        query = """SELECT SUM(amount) as earnings 
                   FROM rides 
                   WHERE strftime('%m', ride_date) = ? 
                   AND strftime('%Y', ride_date) = ?"""
        cursor.execute(query, [month, year])

        earnings = cursor.fetchone()[0]

        if earnings:
            return jsonify({'earnings': earnings})
        else:
            return jsonify({'message': 'No earnings found for given month and year'})

if __name__ == '__main__':
    app.run()

